source.query:
[concat('let ResourceGroupName = "/resourcegroups/', resourceGroup().name, '/";
Perf
| where _ResourceId contains ResourceGroupName
| where ObjectName == "LogicalDisk" 
| where CounterName == "Current Disk Queue Length" 
| where InstanceName <> "_Total" and InstanceName <> "HarddiskVolume1"
//| where TimeGenerated > ago(15m)
| summarize AggregatedValue=avg(CounterValue) by bin(TimeGenerated, 5m), Computer, InstanceName
//| where AggregatedValue  > 5')]

[concat('let ResourceGroupName = "/resourcegroups/', resourceGroup().name, '/";
Perf
| where _ResourceId contains ResourceGroupName
| where ObjectName == "Memory" 
| where CounterName == "Available MBytes" 
//| where TimeGenerated > ago(60m)
| summarize AggregatedValue=avg(CounterValue) by bin(TimeGenerated, 5m), Computer
//| where AggregatedValue < 500')]

[concat('let ResourceGroupName = "/resourcegroups/', resourceGroup().name, '/";
Perf
| where _ResourceId contains ResourceGroupName
| where ObjectName == "Memory" 
| where CounterName == "Pages/sec" 
//| where TimeGenerated > ago(60m)
| summarize AggregatedValue=avg(CounterValue) by bin(TimeGenerated, 5m), Computer
//| where AggregatedValue > 500')]

[concat('let ResourceGroupName = "/resourcegroups/', resourceGroup().name, '/";
Perf
| where _ResourceId contains ResourceGroupName
| where ObjectName == "Memory" 
| where CounterName == "% Committed Bytes In Use" 
//| where TimeGenerated > ago(60m)
| summarize AggregatedValue=avg(CounterValue) by bin(TimeGenerated, 5m), Computer
//| where AggregatedValue > 90')]

[concat('let ResourceGroupName = "/resourcegroups/', resourceGroup().name, '/";
Perf
| where _ResourceId contains ResourceGroupName
| where ObjectName == "Processor" 
| where CounterName == "% Processor Time"
| where InstanceName == "_Total"
//| where TimeGenerated > ago(15m)
| summarize AggregatedValue=avg(CounterValue) by bin(TimeGenerated, 5m), Computer 
//| where AggregatedValue > 85')]
